$(function () {
    console.log('loading alerts');
    $.getJSON('/nms/_get_old_alerts/all/', function(data) {
        for (var alert_num = 0; alert_num < data.alerts.length; alert_num++) {
            alert_info = data.alerts[alert_num]
        
            alert_row = "<tr>" +
            "<td>" + alert_info.device_name + "</td>" + 
            "<td>" + alert_info.param_name + "</td>" + 
            "<td>" + alert_info.message + "</td>" + 
            "<td>" + alert_info.level + "</td>" + 
            "<td>" + alert_info.start_time + "</td>" +
            "</tr>"
            $('#alert_table tr:last').after(alert_row)
        }
    });
});
