/**
 * Set values of data to device parameter representation.
 */
function set_data_to_parameters(device_name){
    var data_url = '/nms/_get_device_last_values/';
    data_url += device_name;
    $.getJSON(data_url, function(data) {
        for (var param_name in data) {
            if (data[param_name] != null) {
                $("#param_value_" + param_name).text(data[param_name]);
                $("#param_value_" + param_name).css("font-weight","Bold");
            }
        }
    });
}
